import React, { useState } from "react";
import { signInUser } from "../../models/user";
import sha256 from "crypto-js/sha256";

const Login = (props) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState(undefined);

    const handlePassword = (pass) => setPassword(sha256(pass).toString());

    const handleClick = () => {
        if (username === "" || password === "") {
            setError("Please fill in all of the required fields.");
            return;
        }
        if (username === "admin" || password === "admin") {

            const payload = {
                email: username,
                password: password,
            };

            signInUser(payload)
                .then((response) => {
                    if (response.status === 200) {
                        props.success(response.data);
                    } else if (response.status === 202) {
                        setError(response.data);
                    }
                })
                .catch((error) => {
                    console.error(error);
                });
        }
        ;

        return (
            <div className="container">
                <h1>Sign-in</h1>

                <div>
                    <label htmlFor="email">Email: </label>
                    <input
                        id="email"
                        alt="email"
                        autoFocus
                        name="username"
                        placeholder="Enter Email Address"
                        type="text"
                        onChange={(event) => setUsername(event.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="password">Password: </label>
                    <input
                        id="password"
                        alt="password"
                        name="password"
                        placeholder="Enter Password"
                        type="password"
                        onChange={(event) => handlePassword(event.target.value)}
                    />
                </div>

                <button aria-label="Sign into Coviforum" onClick={handleClick}>
                    Sign-in
                </button>
                <div onClick={props.changeMode} className="top-right-corner">
                    Registered?
                </div>
                <div className="error">{error ? error : ""}</div>
            </div>
        );
    };
};
export default Login;
