import axios from 'axios';
import authHeader from './auth-header';

const findAllBoards = () => {
  return axios.get("/boards");
};

const findAllPublishedBoards = () => {
  return axios.get("/boards");
};

const getBoards = id => {
  return axios.get(`/boards/${id}`);
};

const createBoards = data => {
  return axios.post("/boards", data);
};

const updateBoards = (id, data) => {
  return axios.put(`/boards/${id}`, data);
};

const deleteBoards = id => {
  return axios.delete(`/boards/${id}`);
};

const deleteAllBoards = () => {
  return axios.delete(`/boards`);
};

const findBoardsByTitle = title => {
  return axios.get(`/boards?title=${title}`);
};

export default {
  findAllBoards,
  findAllPublishedBoards,
  getBoards,
  createBoards,
  updateBoards,
  deleteBoards,
  deleteAllBoards,
  findBoardsByTitle
};
