import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:8080/api/test/';

const findAllBoards = () => {
  return axios.get(API_URL +"/boards");
};

const findAllPublishedBoards = () => {
  return axios.get("/boards", { headers: authHeader() });
};

const getBoards = id => {
  return axios.get(`/boards/${id}`);
};

const createBoards = data => {
  return axios.post("/boards", data, { headers: authHeader() });
};

const updateBoards = (id, data) => {
  return axios.put(`/boards/${id}`, data, { headers: authHeader() });
};

const deleteBoards = id => {
  return axios.delete(`/boards/${id}`, { headers: authHeader() });
};

const deleteAllBoards = () => {
  return axios.delete(`/boards`, { headers: authHeader() });
};

const findBoardsByTitle = title => {
  return axios.get(`/boards?title=${title}`, { headers: authHeader() });
};

export default {
  findAllBoards,
  findAllPublishedBoards,
  getBoards,
  createBoards,
  updateBoards,
  deleteBoards,
  deleteAllBoards,
  findBoardsByTitle
};
