import http from "../http-common";

const getAllBoards = () => {
  return http.get("/api/boards");
};

const getBoardById = id => {
  return http.get(`/api/boards/${id}`);
};

const createBoard = data => {
  return http.post("/api/boards", data);
};

const updateBoard = (id, data) => {
  return http.put(`/api/boards/${id}`, data);
};

const removeBoard = id => {
  return http.delete(`/api/boards/${id}`);
};

const removeAllBoards = () => {
  return http.delete(`/api/boards`);
};

const findBoardByTitle = title => {
  return http.get(`/Boards?title=${title}`);
};

export default {
  getAllBoards,
  getBoardById,
  createBoard,
  updateBoard,
  removeBoard,
  removeAllBoards,
  findBoardByTitle
};
